mapper.insert:
/* accbook.insertAccbook */
INSERT INTO T_ACCBOOK
(
		ACC_ID
	 , USR_ID
	 , CLASS_NM
	 , ITEM_NM
	 , ACC_YMD
	 , ACC_AMT
	 , NOTE
) 
VALUES 
(
	#{accId}
	, 'ADMIN'
	, #{classNm}
	, #{itemNm}
	, #{accYmd}
	, #{accAmt}
	, #{note}
)

mapper.update:
/* accbook.updateAccbook */
UPDATE T_ACCBOOK
SET CLASS_NM = #{classNm}
	, ITEM_NM = #{itemNm}
	, ACC_YMD = #{accYmd}
	, ACC_AMT = #{accAmt}
	, NOTE = #{note}
WHERE ACC_ID = #{accId}
