mapper.insert:
/* run.insertRun */
INSERT INTO T_RUN
(
	  RUN_ID
	, USR_ID
	, RUN_YMD
	, START_DT
	, END_DT
	, DISTANCE
	, DURATION
	, PACE
	, CALORIES_BURNED
	, ROUTE
	, NOTE
	, WEATHER
	, GEAR
	, REG_ID
	, REG_DT
) 
VALUES 
(
	  #{runId}
	, #{usrId}
	, #{runYmd}
	, #{startDt}
	, #{endDt}
	, #{distance}
	, #{duration}
	, #{pace}
	, #{caloriesBurned}
	, #{route}
	, #{note}
	, #{weather}
	, #{gear}
	, #{usrId}
	, NOW()
)

mapper.update:
		/* run.updateRun */
	    UPDATE T_RUN
	    SET   USR_ID = #{usrId}
	        , RUN_YMD = #{runYmd}
	        , START_DT = #{startDt}
	        , END_DT = #{endDt}
	        , DISTANCE = #{distance}
	        , DURATION = #{duration}
	        , PACE = #{pace}
	        , CALORIES_BURNED = #{caloriesBurned}
	        , ROUTE = #{route}
	        , NOTE = #{note}
	        , WEATHER = #{weather}
	        , GEAR = #{gear}
	        , MOD_ID = #{usrId}
	        , MOD_DT = NOW()
         WHERE RUN_ID = #{runId}

mapper.delete:
/* run.updateRun */
DELETE 
FROM T_RUN
WHERE RUN_ID = #{runId}
